class Solution {
    int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int prev[] = new int[1001];
        Arrays.fill(prev, 1);
        for(int i = n-1; i >= 1; i--){
            for(int j = 1; j <= 1000; j++){
                prev[j] = (prev[j]+prev[j-1])%mod;
            }
            int curr[] = new int[1001];
            for(int j = 0; j <= 1000; j++){
                 int val1 = j;
                 int val2 = nums[i-1]-j;
                if(val1 >= 0 && val2 >= 0){
                    int lb = j;
                    int rb = nums[i];
                    int ans = -1;
                    while(lb <= rb){
                        int mid = lb+(rb-lb)/2;
                        if(nums[i]-mid <= val2){
                            ans = mid;
                            rb = mid-1;
                        }
                        else{
                            lb = mid+1;
                        }
                    }
                    if(ans != -1){
                        curr[j] = (prev[nums[i]]-(ans-1 >= 0 ? prev[ans-1] : 0)+mod)%mod;
                    }
                }
            }
            prev = curr;
        }
        if(n == 1) return nums[0]+1;
        int sum = 0;
        for(int it : prev){
            sum = (sum+it)%mod;
        }
        return sum;
    }
}
