class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        if len(nums) == 1: return nums[0] + 1
        current = [1] * (nums[0]+1)
        for i in range(1, len(nums)):
            num, prev = nums[i], nums[i-1]
            newcurrent = [0] * (nums[0] + 1)
            temp = [0] * (nums[0] + 2)
            for small in range(len(current)):
                if min(small + 1, num - (prev - small) + 1) > 0:
                    temp[0] += current[small]
                    if min(small + 1, num - (prev - small) + 1) < len(temp):
                        temp[min(small + 1, num - (prev - small) + 1)] -= current[small]
            running_sum = 0
            for newsmall in range(len(newcurrent)):
                running_sum += temp[newsmall]
                newcurrent[newsmall] = running_sum
            current = newcurrent
        return sum(current)%MOD
