typedef long long int ;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int dp[n+1][1001];
        vector<int> st(n,0);
        int mod=1e9+7;
        memset(dp,0,sizeof(dp));
        int ans=0;
        for(int i=0;i<n;i++){
            if(i==0){
                for(int j=0;j<=nums[i];j++){
                    dp[i][j]=1;
                }
            }
            else{
                int diff= nums[i]-nums[i-1];
                if(diff>=0){
                    st[i]=st[i-1]+diff;
                }
                else{
                    st[i]=st[i-1];
                    diff=0;
                }
                int sum=0;
                vector<int> fif(1001,0);
                for(int j=0;j<=1000;j++){
                    sum+=dp[i-1][j];
                    sum%=mod;
                    fif[j]=sum;
                }
                for(int j=st[i];j<=nums[i];j++){
                    dp[i][j]=fif[j-diff];

                }   
            }
        }
        for(int i=0;i<=1000;i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }
        return ans;
    }
};
