#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001, 0)); 
        vector<vector<int>> prefixSum(n, vector<int>(1001, 0));

        // Initialize the first row of dp array
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
            prefixSum[0][j] = (j == 0 ? dp[0][j] : (prefixSum[0][j-1] + dp[0][j]) % MOD);
        }

        // Fill the dp array
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int arr2 = nums[i] - j;
                if (arr2 >= 0 && arr2 <= nums[i-1]) {
                    dp[i][j] = prefixSum[i-1][min(j, nums[i-1] - arr2)];
                }
                prefixSum[i][j] = (j == 0 ? dp[i][j] : (prefixSum[i][j-1] + dp[i][j]) % MOD);
            }
        }

        // Sum up the last row of dp array to get the result
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
