class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9+7 ;
        vector<vector<long long>> dp(n,vector<long long>(1001,0));
        for(int i = 0;i<=nums[0];i++){
            dp[0][i] = 1 ;
        }
        vector<long long> sumi(1001);
        for(int i = 0 ;i<=nums[0];i++)
          sumi[i] = i+1 ;
        for(int i = nums[0]+1;i<=1000;i++)
          sumi[i] = sumi[i-1];
        for(int i = 1 ;i<n;i++){
            for(int j = 0 ;j<=nums[i];j++){
               int num1 = j ;
               int num2 = (j-nums[i]+nums[i-1]);
              // cout<<num1<<' '<<num2<<' '<<j<<endl;
               num1 = min(num1,num2);
               if(num1<0)
                continue ;
               num1 = min(1000,num1);
               dp[i][j] = sumi[num1];
            }
            sumi[0] = dp[i][0];
            for(int j = 1 ;j<=1000;j++)
             {sumi[j] = sumi[j-1]+dp[i][j];
              sumi[j] %= mod ;}
        }
        // for(int i = 0 ;i<n;i++){
        //     for(int j = 0 ;j<=nums[i];j++)
        //      cout<<dp[i][j]<<' ';
        //     cout<<endl;
        // }
        long long ani = 0 ;
        for(int i = 0 ;i<=nums[n-1];i++){
            ani += dp[n-1][i];
            ani %= mod ;
        }
        int ans = ani ;
        return ans ;
    }
};
