#define LL long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        LL MOD = 1e9+7;
        int n = nums.size();
        vector<vector<LL>> dp(n, vector<LL>(1001, 0));
        vector<vector<LL>> sum(n, vector<LL>(1001, 0));

        for(int i=0;i<=1000;i++) {
            dp[0][i] = 1;
            sum[0][i] += 1;
            if(i!=0) sum[0][i] += sum[0][i-1];

        }

        for(int i=1;i<n;i++) {
            int limit = nums[i] - nums[i-1];
            for(int j=0;j<=nums[i];j++) {
                if(j-limit >= 0) {
                    if(limit >= 0) {
                        dp[i][j] += sum[i-1][j - limit];
                    } else {
                        dp[i][j] += sum[i-1][j];
                    }

                }

                dp[i][j] %= MOD;
                sum[i][j] = dp[i][j];
                if(j != 0)
                sum[i][j] += sum[i][j-1];
                sum[i][j] %= MOD;
            }
        }

        // LL ans = 0;
        // for(int i=0;i<=nums[n-1];i++) {
        //     ans += dp[n-1][i];
        //     ans %= MOD;
        // }
        // cout << ans<<endl;
        return sum[n-1][nums[n-1]];
    }
};

