#define ll long long
const ll MOD = (ll)1e9 + 7LL;
class Solution {
public:
    int countOfPairs(vector<int>& A) {
        ll N = A.size();
        vector<ll> dp(1010), p;
        for(ll i = 0; i <= A[0]; ++i) dp[i] = 1;
        p = dp;
        for(ll i = 1; i < 1010; ++i) p[i] = (p[i] + p[i - 1]) % MOD;
        for(ll i = 1; i < N; ++i) {
            vector<ll> ndp(1010);
            for(ll j = 0; j <= A[i]; ++j) {
                ll k = min(A[i - 1] - A[i] + j, j);
                if(k >= 0)
                    ndp[j] += p[k];
                ndp[j] %= MOD;
                // for(ll k = 0; k <= j; ++k) if(A[i - 1] - k >= A[i] - j) {
                //     /*A[i - 1] - k >= A[i] - j
                //     k <= A[i - 1] - (A[i] - j)
                //     k <= A[i - 1] - A[i] + j;*/
                //     ndp[j] += dp[k];
                //     ndp[j] %= MOD;
                // }
            }
            swap(dp, ndp);
            p = dp;
            for(ll i = 1; i < 1010; ++i) p[i] = (p[i] + p[i - 1]) % MOD;
        }
        ll ans = 0;
        for(auto x : dp)
            ans = (ans + x % MOD) % MOD;
        return ans;
    }
};
