class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        length = 1001
        dp = [0] * length
        acc = [0] * length
        def update_acc():
            acc[0] = dp[0]
            for i in range(1, length):
                acc[i] = acc[i-1]+dp[i]

        num = nums[0]
        for x in range(num+1):
            dp[x] = 1
        update_acc()

        # print("dp", dp)
        # print("acc", acc)

        prev_num = nums[0]
        for num in nums[1:]:
            dp_new = [0] * length
            if num >= prev_num:
                for x in range(num+1):
                    if x >= num - prev_num:
                        dp_new[x] = acc[x - (num - prev_num)]
            else:
                for x in range(num+1):
                    dp_new[x] = acc[x]

            dp = dp_new
            update_acc()
            prev_num = num

            # print("dp", dp)
            # print("acc", acc)



        const = 10 ** 9 + 7
        return sum(dp) % const
