#define ll long long
const int M = 1e9 + 7;
ll dp[2002][1001];
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<=nums.size();i++){
            for(int j=0;j<=1000;j++){
                dp[i][j] = 0;
            }
        }
        for(int j=0;j<=1000;j++){
            dp[0][j] = 1;
        }
        vector<ll> pref(1001);
        for(int j=0;j<=nums[0];j++){
            dp[1][j] = 1;
        }
        pref[0] = dp[1][0];
        for(int j=1;j<=1000;j++){
            pref[j] = (dp[1][j] + pref[j - 1])%M;
        }
        for(int i=2;i<=nums.size();i++){
            // cout<<"For i: "<<i<<endl;
            for(int j=0;j<=nums[i-1];j++){
                ll st = 0;
                ll en = j;
                ll k = nums[i-1]-j;
                ll ans = -1;
                // cout<<k<<endl;
                while(st <= en){
                    ll mid = (st + en)/2;
                    ll k2 = nums[i - 2] - mid;
                    // cout<<mid<<" "<<k2<<endl;
                    if(k2 >= k){
                        ans = mid;
                        st = mid + 1;
                    }else{
                        en = mid - 1;
                    }
                }
                if(ans != -1)
                dp[i][j] = (dp[i][j] + pref[ans]);
                // cout<<j<<" "<<ans<<" "<<dp[i][j]<<endl;
            }
            // cout<<endl;
            pref[0] = dp[i][0];
            for(int j=1;j<=1000;j++){
                pref[j] = (dp[i][j] + pref[j - 1])%M;
            }
        }
        return pref[nums.back()];
    }
};
