#define ll long long

class Solution {
public:
    const ll MOD = 1e9 + 7;
    const int MAXK = 1001;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<ll>> dp(n, vector<ll>(MAXK, 0));

        for (int k = 0; k <= nums[0]; k++) {
            dp[0][k] = 1;
        }

        for (int i = 1; i < n; i++) {
            int off = 0;
            if (nums[i-1] - nums[i] >= 0) {
                dp[i][0] = dp[i-1][0];
            } else {
                for (int k = 0; k < nums[i] - nums[i-1]; k++) {
                    dp[i][k] = 0;
                }
                off = nums[i] - nums[i-1];
            }
            // cout << off << endl;
            dp[i][off] = dp[i-1][0];
            for (int k = off+1; k <= nums[i]; k++) {
                // cout << k-off << endl;
                dp[i][k] = (dp[i][k-1] + dp[i-1][k-off]) % MOD;

                // for (int ki = 0; ki <= nums[i-1]; ki++) {
                //     if (ki <= min(nums[i-1] - nums[i] + k, k)) {
                //         dp[i][k] = (dp[i][k] + dp[i-1][ki]) % MOD;
                //     }
                // }
            }
        }

        ll total = 0;
        for (int i = 0; i < MAXK; i++) {
            total = (total + dp[n-1][i]) % MOD;
        }

        return (int) total;
    }
};
