class Solution {
public:
    static constexpr int mod = 1e9+7;
    int add(int x,int y){
        if((x+=y)>=mod)x-=mod;
        return x;
    }
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size(), mxn = 1010;
        vector<int> pre1(mxn,0), pre2(mxn,0);
        for(int i=0;i<=nums[0];++i) pre1[i] = 1;

        for(int i=1;i<n;++i){
            for(int j=0;j<mxn;++j) pre2[j] = 0;
            for(int j=0,tt=0,it=0;j<=nums[i];++j){
                for(;it<=min(j,nums[i-1]) && j-it >= max(0, nums[i] - nums[i-1]);++it){
                    tt = add(tt, pre1[it]);
                }
                pre2[j] = tt;
            }
            pre1.swap(pre2);
        }
        int ret=0;
        for(int i=0;i<mxn;++i) ret = add(ret, pre1[i]);
        return ret;
    }
};
