class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        int n=nums.size(),m=1e9+7;
       vector<int>dp(1001,0);
        for(int v=0;v<=1000;v++)
        {
           if(v<=nums[0]) dp[v]=v+1;
           else dp[v]=dp[v-1];
        }
        for(int i=1;i<n;i++)
        {
            vector<int>temp(1001,0);
           for(int vc=0;vc<=nums[i];vc++)
           {
              int mi=min({vc,nums[i-1]-nums[i]+vc,nums[i-1]});
             // mi=max(mi,0);
             if(mi>=0) temp[vc]=dp[mi];
           }
           for(int vc=1;vc<=1000;vc++) temp[vc]=(temp[vc]+temp[vc-1])%m;
           dp=temp;
        }
        return dp[1000];
    }
};
