class Solution {
    void madd(int& x,int y){
        constexpr int P=1'000'000'007;
        x+=y;
        if(x>=P)
            x-=P;
    }
    void psum(vector<int>& a){
        for(int i=1;i<a.size();i++)
            madd(a[i],a[i-1]);
    }
public:
    int countOfPairs(vector<int>& nums) {
        vector<int> dps(nums[0]+1,1);
        psum(dps);
        for(int t=1;t<nums.size();t++){
            vector<int> dp2(nums[t]+1);
            for(int i=0;i<=nums[t];i++){
                int r=min({i,nums[t-1]-(nums[t]-i),nums[t-1]});
                if(r>=0)
                    dp2[i]=dps[r];
            }
            psum(dp2);
            swap(dps,dp2);
        }
        return dps.back();
    }
};
