class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1E9 + 7;
        long long n = nums.size();
        // [index][arr1 value]
        vector<vector<long long>> v(n, vector<long long>());
        for (long long i = 0; i <= nums[0]; i++) {
            v[0].push_back(1);
        }
        for (long long i = 1; i < n; i++) {
            // how much a1 at least should > preva1
            long long mindiff = max(0, nums[i] - nums[i - 1]);

            vector<long long> prefixsum;
            long long running = 0;
            for (long long k = 0; k < v[i - 1].size(); k++) {
                running += v[i - 1][k];
                running %= mod;
                prefixsum.push_back(running);
            }

            for (long long a1 = 0; a1 <= nums[i]; a1++) {
                // calc v[i][a1];
                long long maxpreva1 = a1 - mindiff;

                if (maxpreva1 < 0) {
                    v[i].push_back(0LL);
                } else if (maxpreva1 >= prefixsum.size()) {
                    v[i].push_back(prefixsum.back());
                } else {
                    v[i].push_back(prefixsum[maxpreva1]);
                }

                // for (long long preva1 = 0; preva1 <= nums[i - 1]; preva1++) {
                //     long long preva2 = nums[i - 1] - preva1;
                //     // validate
                //     if (preva1 > a1 || preva2 < a2) {
                //         continue;
                //     }
                //     v[i][a1] += v[i - 1][preva1];
                //     v[i][a1] %= mod;
                // }
            }
        }

        // for (auto row : v) {
        //     for (auto k : row) {
        //         cout << k << " ";
        //     }
        //     cout << endl;
        // }

        // change me
        long long ans = 0LL;
        for (auto tmp : v[n - 1]) {
            ans += tmp;
            ans %= mod;
        }
        return ans;
    }
};
