class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int count[1001][2001] = {0};
        for (int i = 0; i < 1001; ++i) {
            count[i][0] = 1;
        }

        int mod = 1e9 + 7;
        for (int i = 1; i < nums.size(); ++i) {
            for (int j = 1; j < 1001; ++j) {
                count[j][i - 1] = (count[j][i - 1] + count[j - 1][i - 1]) % mod;
            }

            for (int a = max(0, nums[i] - nums[i - 1]); a <= nums[i]; ++a) {
                count[a][i] = (count[min(a, nums[i - 1] - nums[i] + a)][i - 1] + count[a][i]) % mod;
            }
        }

        int res = 0;
        for (int i = 0; i <= nums.back(); ++i) {
            res = (res + count[i][nums.size() - 1]) % mod;
        }
        return res;
    }
};
