class Solution {
    typedef long long ll;
    const int M = 1e9 + 7;
    typedef vector<ll> vi;
    ll dp[2001][1001] = {0};
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        ll mindiff = 0;
        vi v(n,0);
        for(ll i=1;i<n;i++){
            if(nums[i]>nums[i-1]){
                mindiff+= nums[i]-nums[i-1];
            }
            v[i] = mindiff;
        }
        for(int i=0;i<=nums[n-1] - v[n-1];i++){
            dp[n-1][i] = 1;  //6 ,5 4, 3, 2, 1, 0.
        }
        for(int i=n-2;i>=0;i--){
            ll cs = 0;
            for(int j=nums[i+1] - v[i+1];j>=0;j--){
                cs = (cs+ dp[i+1][j])%M;
                dp[i][j] = cs;
            }
        }
        ll ans = 0;
        for(int i=0;i<=nums[0]-v[0];i++){
            ans = (ans + dp[0][i])%M;
        }
        return ans;
    }
};
