class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),i,j,k,mod=1e9+7;
        vector<vector<long long>> dp(n,vector<long long>(1002,0));
        for(i=0;i<=nums[0];i++){
                dp[0][i]=1;
        }
        for(i=1;i<n;i++){
            int x=nums[i-1],y=nums[i];
            // for(j=0;j<=x;j++){
            // vector<int> pre(52,0);
            // pre[0]=0;
            // for(j=1;j<52;j++){
            //     pre[j]=(pre[j-1]+dp[i-1][j-1])%mod;
            //     cout<<pre[j]<<" ";
            // }
            // cout<<"\n";
            int diff = nums[i]-nums[i-1];
            if(diff<=0){
                dp[i][0]=dp[i-1][0];
                for(k=1;k<=y;k++){
                        // cout<<k<<":"<<l<<" "<<r<<endl;
                    dp[i][k]=(dp[i-1][k]+dp[i][k-1])%mod;
                }
            }
            else{
                for(k=diff;k<=y;k++){
                        // cout<<k<<":"<<l<<" "<<r<<endl;
                    dp[i][k]=(dp[i-1][k-diff]+dp[i][k-1])%mod;
                }
            }
            // }
        }
        long long ans=0;
        for(i=0;i<1002;i++){
            ans=(ans+dp[n-1][i])%mod;
        }
        // for(i=0;i<n;i++){
        //     for(j=0;j<51;j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<"\n";
        // }
        // cout<<"\n";
        return ans;
    }
};
