class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [1]*(nums[0]+1)
        for idx in range(len(dp)):
            dp[idx] = idx+1
        # print(dp)
        module = 1000000007
        for pre_num, num in zip(nums, nums[1:]):
            _dp = [0]*(num+1)
            # print("-", num)
            # print([num1 for num1 in range(num+1)  if num-num1<=pre_num])
            for cnt, num1 in zip(dp,[num1 for num1 in range(num+1)  if num-num1<=pre_num]):
                _dp[num1] = (cnt +  (_dp[num1-1] if num1>0 else 0))%module
            dp = _dp
            # print("*", dp)
        return dp[-1]%module
