class Solution {
public:
    void fill(vector<int> &nums, vector<vector<int>> &memo, int col){
        if(col==(nums.size()-1)){
            for(int i=0; i<=nums[col]; ++i){
                memo[col][i]=1;
            }
            return;
        }
        fill(nums, memo, col+1);
        int sum=0;
        int j=0;
        for(int i=0; i<=nums[col]; ++i){
            for(; j<=i; ++j){
                if((nums[col+1]-j)<(nums[col]-i)){
                    break;
                }
                sum += memo[col+1][j];
                sum %= 1000000007;
            }
            memo[col][i] = sum;
        }
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> memo(n, vector<int>(1001,0));
        fill(nums, memo, 0);
        int ans = 0;
        for(int i=0; i<1001; ++i){
            ans += memo[0][i];
            ans %= 1000000007;
        }
        return ans;
    }
};
