class Solution(object):
    def countOfPairs(self, nums):
        if len(nums) == 1:
            return nums[0] + 1

        old_dp = [1 for i in range(1001)]

        mod = 10 ** 9 + 7

        for i in range(len(nums) - 2, -1, -1):
            new_dp = [0] * 1001
            for x in range(1, len(old_dp)):
                old_dp[x] += old_dp[x - 1]
            old_dp.append(0)

            for v in range(nums[i] + 1):


                new_dp[v] = old_dp[max(min(v, v + nums[i + 1] - nums[i]), -1)]
                new_dp[v] %= mod


            old_dp = new_dp



        return sum(old_dp) % mod
