class Solution {
public:
    long dp[2001][1001], psum[1001];
    int mod = 1000000007;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        for(int j=0; j<=nums[0]; j++) dp[0][j]=1;
        for(int j=0; j<1001; j++){
            psum[j] = dp[0][j];
            if(j) psum[j] += psum[j-1];
        }

        for(int i=1; i<n; i++){
            for(int j=0; j<=nums[i]; j++){
                int prevmin = 0;
                int prevmax = min(j+nums[i-1]-nums[i], j);
                if(prevmin<=prevmax){
                    dp[i][j] = psum[prevmax];
                }
            }
            for(int j=0; j<1001; j++){
                psum[j] = dp[i][j];
                if(j) psum[j] += psum[j-1];
                psum[j] %= mod;
            }
        }

        long ans = 0;
        for(int j=0; j<=nums[n-1]; j++){
            ans += dp[n-1][j];
            ans %= mod;
        }
        return int(ans);
    }
};
