class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0 for i in range(max(nums)+1)] for j in range(n)]
        cum = [[0 for i in range(max(nums)+1)] for j in range(n)]

        MOD = 1000_000_000 + 7
        for i in range(nums[0]+1):
            dp[0][i] = 1
            cum[0][i] = (cum[0][i-1]+dp[0][i]) % MOD if i!=0 else dp[0][0]
        for i in range(1,n):
            for k in range(nums[i]+1):
                a, b = k, nums[i] - k
                start = min(nums[i-1]-b,a)
                if start<0:
                    continue
                dp[i][k] = (dp[i][k]+cum[i-1][start])%MOD
                cum[i][k] = (cum[i][k-1]+dp[i][k]) % MOD if k!=0 else dp[i][0]
        return sum(dp[n-1])%MOD
