class Solution {
    private static final int MOD = 1_000_000_007;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int max=0;
        for(int i=0;i<n;i++){
            max=Math.max(max,nums[i]);
        }
        long dp[][]=new long[n][max + 1];
        for(int j=0;j<=nums[0];j++){
            dp[0][j]=1l;
        }
        for(int i=1;i<n;i++){
            long sum=0;
            for(int j=0;j<=nums[i];j++){
                int st=Math.max(j,nums[i]-nums[i-1]+j);
                sum=(sum+dp[i-1][j])%MOD;
                for(int k=st;k<=nums[i];k++){
                    dp[i][k]=sum;
                }

            }
        }
        long result=0l;
        for(int k=0;k<=max;k++){
            result=(result+dp[n-1][k])%MOD;
        }
        return (int) result;
    }
}
