class Solution {
public:

    int countOfPairs(vector<int>& nums) {
        long long MOD = 1e9+7;
        int n = nums.size();
        vector<vector<long long>> dp(n);
        vector<long long> rsum(2001,0);
        for(int i=0; i<n; i++) for(int j=0; j<2001; j++) dp[i].push_back(0);

        // dp[i][j]
        // no. of valid answer until nums[i]
        // j is the value of ascending array at point nums[i]

        // initial condition
        for(int j=0; j<2001; j++){
            int a = j;
            int d = nums[0]-a;
            if((a>=0) && (d>=0)) dp[0][j]++;

            if(j == 0) rsum[j] = dp[0][j];
            else rsum[j] = (rsum[j-1] + dp[0][j])%MOD;
        }

        // loop
        for(int i=1; i<n; i++){
            for(int j=0; j<2001; j++){
                int a = j;
                int d = nums[i]-a;

                if(d < 0) continue;

                int a1 = 2001;
                a1 = min(a1, a);
                a1 = min(a1, nums[i-1]);
                a1 = min(a1, nums[i-1]-d);

                if(a1 < 0) continue;
                dp[i][j] = rsum[a1];
            }

            for(int j=0; j<2001; j++){
                if(j == 0) rsum[j] = dp[i][j];
                else rsum[j] = (rsum[j-1] + dp[i][j])%MOD;
            }
        }

        long long ans = 0;
        // final sum
        for(int j=0; j<2001; j++) ans = (ans + dp[n-1][j])%MOD;

        return ans;
    }

};
