#include<bits/stdc++.h>
#include<fstream>
using namespace std;
#define sz(a) (int)a.size()
#define ALL(v) v.begin(), v.end()
#define ALLR(v) v.rbegin(), v.rend()
#define ll long long
#define pb push_back
#define forr(i, a, b) for(int i = a; i < b; i++)
#define dorr(i, a, b) for(int i = a; i >= b; i--)
#define ld long double
#define vt vector
#include<fstream>
#define fi first
#define se second
#define pll pair<ll, ll>
#define pii pair<int, int>
#define mpp make_pair
const int mod = 1e9 + 7;
class Solution {
public:
    int dp[2005][1005], pref[2005][1005];
    void add(int &a, int b){
        if(b < 0)b += mod;
        a += b;
        if(a >= mod)a -= mod;
    }
    int countOfPairs(vector<int>& nums) {
        for(int i = 0; i <= sz(nums); i++){
            for(int j = 0; j <= 1001; j++){
                dp[i][j] = 0;
            }
        }
        for(int i = 0; i <= nums[0]; i++){
            dp[0][i] = 1; 
        }
        for(int i = 0; i <= 1000; i++){
            pref[0][i + 1] = pref[0][i] + dp[0][i];
        }
        for(int i = 0; i < sz(nums) - 1; i++){
            for(int j = 0; j <= 1000; j++){
                int arr2 = nums[i] - j; 
                //nums[i + 1] - arr1 <= arr2

                int mxv = nums[i + 1], mnv = max(j, nums[i + 1] - arr2);
                if(mnv <= mxv){
                    add(pref[i + 1][mnv], dp[i][j]); add(pref[i + 1][mxv + 1], -dp[i][j]);
                }
            }
            for(int j = 0; j <= 1000; j++){
                if(j)add(pref[i + 1][j], pref[i + 1][j - 1]);
                dp[i + 1][j] = pref[i + 1][j];
                //cout << dp[i + 1][j] << " ";
            }
           // cout << "\n";
        }
        int ans = 0;
        for(int i= 0; i <= 1000; i++)add(ans, dp[sz(nums) - 1][i]);
        return(ans);
    }
};
