class Solution {
public:
    int bs(int limit, int val ){

        int low=0;
        int high=val;
        int ans=-1;

        while(low<=high){

            int mid= (low+high)/2;
            int val2= val-mid;

            if(val2>=limit){
                ans=mid;
                low=mid+1;
            }else{
                high=mid-1;
            }
        }
        return ans;

    }
    int countOfPairs(vector<int>& arr) {

        int n= arr.size();
        int maxVal=0;
        for(int i=0; i<n;i++){
            maxVal= max(maxVal, arr[i]);
        }
        vector<vector<int>>dp(n, vector<int>(maxVal+10,0));
        int mod=1e9+7;

        for(int i=0; i<n;i++){


            for(int j=0; j<dp[0].size();j++){

                  if(i==0 && j==0){
                      dp[i][j]=1;
                      continue;
                  }else if(i==0){

                      if(j>arr[i]){
                          dp[i][j]=dp[i][j-1];
                      }else{
                          dp[i][j]=(1*1L+1L*dp[i][j-1])%mod;
                      }
                      continue;
                  }

                if(j>arr[i]){

                    dp[i][j]= dp[i][j-1];
                }else{

                    int index= bs(arr[i]-j,arr[i-1]);
                    // cout<<i<<" "<<arr[i]-j<<" "<<index<<endl;
                    index= min(j, index);
                    if(j!=0 && index !=-1 ){
                        dp[i][j]= (1L*dp[i][j-1]+ 1L*dp[i-1][index])%mod;
                    }else if(index !=-1){
                        dp[i][j]= dp[i-1][index];
                    }
                }

            }
        }

//         for(int i=0; i<n;i++){

//             for(int j=0; j<dp[0].size();j++){

//                 cout<<dp[i][j]<<" ";
//             }
//             cout<<endl;
//         }
        return dp[n-1][arr[n-1]];

    }
};
