class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7
        dp_cur = [0] * 1002
        dp_cur[0] += 1
        dp_cur[nums[0] + 1] -= 1
        for i in range(1, 1002):
            dp_cur[i] += dp_cur[i - 1]

        for j in range(1, n):
            dp_nxt = [0] * 1002
            diff = nums[j] - nums[j - 1]
            for i in range(nums[j - 1] + 1):
                if diff >= 0:
                    dp_nxt[i + diff] += dp_cur[i]
                    # print(diff + nums[j - 1] + 1)
                    dp_nxt[diff + nums[j - 1] + 1] -= dp_cur[i]
                else:
                    if nums[j - 1] - i + diff >= 0:
                        dp_nxt[i] += dp_cur[i]
                        dp_nxt[i + nums[j - 1] - i + diff + 1] -= dp_cur[i]

            for i in range(1, 1002):
                dp_nxt[i] += dp_nxt[i - 1]
                dp_nxt[i] %= MOD

            dp_cur = dp_nxt

        ans = 0
        for val in dp_cur:
            ans += val
            ans %= MOD
        return ans
