class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0]*1001 for _ in range(n)]
        for i in range(1001): dp[0][i]=(1 if i <= nums[0] else 0)+ (dp[0][i-1] if i>0 else 0)

        for i in range(1, n):
            for j in range(1001):
                if j >0: dp[i][j] = dp[i][j-1]
                if j > nums[i]: continue
                last = min(j, nums[i-1]-nums[i]+j)
                if last>=0:
                    dp[i][j] = (dp[i][j]+dp[i-1][last])%(int(1e9)+7)
        # print(dp)
        return dp[n-1][1000]
