class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [0] * (max(nums) + 1)
        tmp = 1
        for j in range(len(dp)):
            if j <= nums[0]:
                dp[j] = tmp
                tmp += 1
            else:
                break

        prev_n = nums[0]
        # print(dp)
        for n in nums[1:]:
            new_dp = [0] * (max(nums) + 1)
            if prev_n < n:
                offset = n - prev_n
                for j in range(offset, min(len(dp), n+1)):
                    new_dp[j] = new_dp[j-1] + dp[j-offset]
            else:
                for j in range(n + 1):
                    if j == 0:
                        new_dp[j] = dp[j]
                    else:
                        new_dp[j] = new_dp[j-1] + dp[j]
            dp = new_dp
            prev_n = n
            # print(dp)

        return max(dp) % (10 ** 9 + 7)
