func countOfPairs(nums []int) int {
    n := len(nums)
    const M int = 1e9+7
    mx := slices.Max(nums)

    dp := make([][]int, n)
    for i := range dp {
        dp[i] = make([]int, mx+1)
    }

    for i:=0; i<=nums[0]; i++ {
        dp[0][i] = 1
    }



    for i:=1; i<n; i++ {
        diff := make([]int, mx+5)
        for a:=0; a<=nums[i-1]; a++ {
            x := max(a, a+nums[i]-nums[i-1])
            diff[x] += dp[i-1][a]
            diff[nums[i]+1] -= dp[i-1][a]
        }
        sum := 0
        for d:=0; d<=nums[i]; d++ {
            sum += diff[d]
            dp[i][d] += sum
            dp[i][d] %= M
        }
    }

    res := 0
    for i:=0; i<=nums[n-1]; i++ {
       res += dp[n-1][i]
        res %= M
    }
    return res   
}
