class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int m = 1e9 + 7;

        vector<vector<int>> dp(n, vector<int> (1002, 0));

        for(int j=1; j<=nums[0]+1; j++){
            dp[0][j] = 1 + dp[0][j-1];
        }
        for(int j= nums[0]+2; j<=1001; j++) dp[0][j] = dp[0][j-1];

        for(int i=1; i<n; i++){
            int z = nums[i-1];

            for(int j = 1; j<=nums[i]+1; j++){
                int x = j-1;
                int y = nums[i-1] - (nums[i] -j+1);
                if(y<0) continue;
                x = min(x, y) +1;

                dp[i][j] = (dp[i][j-1] + dp[i-1][x])%m;
            }
            for(int j=nums[i]+2; j<=1001; j++) dp[i][j] = dp[i][j-1];

        }
        return dp[n-1][1001];
    }
};
