class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mod=1e9+7;
        int dp[n][1001];
        int pref[n][1001];
        memset(dp,0,sizeof(dp));
        memset(pref,0,sizeof(pref));
        for(int i=0;i<n;i++){
            int val=nums[i];
            for(int j=0;j<=val;j++){
                int cur1=j,cur2=(val-j);
                if(i>0){
                    int prev2L=(nums[i-1]-j);
                    int leftL=-1;
                    int rightR=j;
                    if(prev2L<cur2) {
                        int diff=(cur2-prev2L);
                        rightR-=diff;
                    }
                    if(rightR>=0){
                    dp[i][j]=(pref[i-1][rightR]-(leftL>=0 ? pref[i-1][leftL]: 0));
                    dp[i][j]%=mod;
                    }                    
                }
                else{
                    dp[i][j]=1;
                }
            }
            for(int j=0;j<=val;j++) {pref[i][j]=dp[i][j]+(j==0 ? 0 : pref[i][j-1]);pref[i][j]%=mod;}
        }
        int ans=0;
        for(int i=0;i<=1000;i++) {ans+=(dp[n-1][i]);ans%=mod;}
        return ans;
    }
};
