class Solution {

    int MOD = (int)1e9 + 7;

    public int mod(long a, long b){
        return (int)((a + b) % MOD);
    }

    public int countOfPairs(int[] nums) {
        int[] dp = new int[1002];
        int n = nums.length;

        for(int i = nums[n-1] ; i >= 0 ; i--){
            dp[i] = dp[i+1] + 1;
        }
        // System.out.println(Arrays.toString(dp));

        for(int i = n-2 ; i >= 0 ; i--){
            int[] ndp = new int[1002];

            for(int j = nums[i] ; j >= 0 ; j--){
                int a = j;
                int b = nums[i]-a;

                int b2 = nums[i+1]-a;

                if(b2 > b){
                    a += b2-b;
                }

                if(a <= 1000){
                    ndp[j] = mod(ndp[j+1], dp[a]);
                }
            }

            dp = ndp;
        }


        return dp[0];
    }
}
