class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long int MOD = 1e9 + 7;
        int i, j, k, p, q, p2;
        vector<vector<long long int> > dp(n, vector<long long int>(1001, 0));
        vector<vector<long long int> > cum_dp(n, vector<long long int>(1001, 0));

        i = 0;
        for(j = 0; j <= nums[i]; j++) {
            dp[i][j] = 1;
            if(j > 0) {
                cum_dp[i][j] = cum_dp[i][j - 1] + dp[i][j];
            } else {
                cum_dp[i][j] = dp[i][j];
            }
        }

        // 2, 8, 5
        //    


        for(i = 1; i < n; i++) {
            // cout << i << "\n";
            for(j = 0; j <= nums[i]; j++) {
                k = nums[i] - j;
                p2 = nums[i - 1] - k;
                p = min(j, nums[i - 1]);
                p = min(p, p2);
                // cout << j << ": " << p << ": ";

                if(p >= 0) {
                    dp[i][j] = (dp[i][j] + cum_dp[i - 1][p]) % MOD;
                } else {
                    dp[i][j] = 0;
                }
                // cout << dp[i][j] << " | ";
                // for(p = 0; p <= nums[i - 1]; p++) {
                //     q = nums[i - 1] - p;
                //     if(p <= j && q >= k) {
                //         dp[i][j] = (dp[i][j] + dp[i - 1][p]) % MOD;
                //     }
                // }

                if(j > 0) {
                    cum_dp[i][j] = (cum_dp[i][j - 1] + dp[i][j]) % MOD;
                } else {
                    cum_dp[i][j] = dp[i][j];
                }

            }
            // cout << endl;
        }

        // long long int ans = 0;
        // for(j = 0; j <= nums[n - 1]; j++) {
        //     ans = (ans + dp[n - 1][j]) % MOD;
        // }
        // return ans;
        return cum_dp[n - 1][nums[n - 1]];
    }
};
