class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        def dfs(index, arr1, arr2):
            if arr2 < 0:
                return 0
            if dicti[index][arr1] is None:
                if index == n:
                    dicti[index][arr1] = 1
                elif arr1 > nums[index]:
                    dicti[index][arr1] = 0
                else:
                    dicti[index][arr1] = (
                        dfs(index, arr1 + 1, arr2 - 1) + (
                            dfs(index + 1, arr1, nums[index] - arr1) if nums[index] - arr1 <= arr2 else 0
                        ) + (
                            dfs(index + 1, nums[index] - arr2, arr2) if nums[index] - arr2 > arr1 else 0
                        )
                    ) % mod
            return dicti[index][arr1]

        mod, n = pow(10, 9) + 7, len(nums)
        dicti = [[None] * 1001 for _ in range(n + 1)]
        return sum(dfs(1, i, nums[0] - i) for i in range(nums[0] + 1)) % mod
