class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[1 for i in range(nums[0] + 1)]]
        prev = nums[0]

        for idx in range(1, len(nums)):
            val = nums[idx]
            temp = []
            count = 0
            j = 0
            for i in range(val + 1):
                while j < prev + 1 and i >= j and val - i <= prev - j:
                    count += dp[-1][j]
                    j += 1
                temp.append(count)
            prev = len(temp) - 1
            dp.append(temp)


        return sum(dp[-1]) % (10**9 + 7)
