class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        limit = max(nums)
        dp = [[0] * (limit + 1) for _ in range(n+1)]
        dp[0][0] = 1
        for i, v in enumerate(nums, 1):
            k = 0
            for j in range(v+1):
                upper = j
                if i > 1:
                    upper = min(upper, -v + j + nums[i-2])
                # if upper < 0:
                    # break
                if j > 0:
                    dp[i][j] = dp[i][j-1]
                while k <= upper:
                    dp[i][j] = (dp[i][j] + dp[i-1][k]) % 1000000007
                    k += 1
            # print(dp[i])
        return sum(dp[n]) % 1000000007
