public class Solution {
    public int CountOfPairs(int[] nums) {
                    const int mode = 1_000_000_007;
            long[] dp1 = new long[1001], dp2 = new long[1001];
            for (int i = 0; i < 1001; i++)
            {
                dp1[i] = 1;
            }

            var n = nums.Length;
            for (int i = 0; i < n; i++)
            {
                var pre = i > 0 ? nums[i - 1] : nums[0];
                for (int j = 0; j < 1001; j++)
                {
                    var cur = Math.Min(pre - nums[i] + j, j);
                    if (cur < 0)
                        dp2[j] = 0;
                    else
                    {
                        dp2[j] = dp1[cur];
                    }

                    if (j > 0)
                        dp2[j] = (dp2[j] + dp2[j - 1]) % mode;
                }

                (dp1, dp2) = (dp2, dp1);
            }

            return (int) dp1[nums[n - 1]];
    }
}
