class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int N = 1e9+7;
        vector<vector<int>> dp(n, vector<int> (1001, 0));
        vector<vector<int>> dp2(n, vector<int> (1001, 0));
        int sm  = 0;
        for(int i = n - 1; i >= 0; i--) {
            for(int j = 1000; j >= 0; j--) {

                    int x = nums[i] - j;
                    if(x < 0) continue;
                    if( i == n - 1) {
                        dp[i][j] = (dp[i][j] + 1) % N;
                        dp2[i][j] = dp[i][j];
                        if(j != 1000)
                        dp2[i][j] = (dp2[i][j] + dp2[i][j+1]) % N;
                        continue;
                    }
                    int k = max(0, nums[i+1] - x);
                    k = max(k, j);       
                    dp[i][j] = (dp[i][j] + dp2[i+1][k]) % N;
                    dp2[i][j] = dp[i][j];
                    if(j != 1000) dp2[i][j] = (dp2[i][j] + dp2[i][j+1]) % N;


            }
        }
        return dp2[0][0];
    }
};
