class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        prev = [0] * (nums[0]+1)
        for v in range(nums[0]+1):
            if v == 0:
                prev[v] = 1
            else:
                prev[v] = prev[v-1]+1
        # print(prev)
        n1 = nums[0]
        for n2 in nums[1:]:
            cur = []
            prev_sum = 0
            for y in range(0, n2+1):
                dy = max(0, n2 - n1)
                x = y - dy
                x = min(x, len(prev)-1)
                if x >= 0:
                    prev_sum += prev[x]
                    prev_sum %= (10**9+7)
                cur.append(prev_sum)
            n1 = n2
            # print(cur)
            prev = cur
        return prev[-1]
