class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = max(nums)+1
        n = len(nums)
        dp = [0]*m
        dp2 = [0]*m
        for i in range(nums[0]+1):
            dp[i]=1
            dp2[nums[0]-i]=1
        # print(dp,dp2)
        for j in range(1,m):
            dp[j]+=dp[j-1]
            dp2[m-1-j]+=dp2[m-j]
        # print(dp,dp2)
        for i in range(1,n):
            dpp = [0]*m
            dpp2 = [0]*m
            for j in range(nums[i]+1):
                dpp[j] += min(dp[j], dp2[nums[i]-j])
                dpp2[j] += min(dp[nums[i]-j], dp2[j])

            for j in range(1,m):
                dpp[j]+=dpp[j-1]
                dpp2[m-1-j]+=dpp2[m-j]
            dp, dp2 = dpp, dpp2

        # print(dp,dp2)
        return dp[nums[-1]]%(10**9+7)
