#define rep(i,n) for(int i=0;i<n;i++)
#define rng(i,a,b) for(int i=a;i<b;i++)
#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
        vector<vector<ll>> dp(n, vector<ll>(1001, 0));
        ll mod = 1e9 + 7;
        vector<ll> p_sum(1001,0);
        rep(i,nums[0]+1){
            dp[0][i]=1;
        }
        rep(i,1001){
            p_sum[i]=(i>0?p_sum[i-1]:0)+dp[0][i];
        }
        rng(i,1,n){
            rep(j,nums[i]+1){
                ll r = min(j+1,nums[i-1]-nums[i]+j+1)-1;
                if(r>=0 && r<1001)
                    (dp[i][j]+=p_sum[r])%=mod;
            }
            rep(j,1001){
                p_sum[j]=0;
            }
            rep(j,1001){
                p_sum[j]=(j>0?p_sum[j-1]:0)+dp[i][j];
            }
        }
        ll ans = 0;
        rep(i,nums[n-1]+1){
            (ans+=dp[n-1][i])%=mod;      
        }
        return ans;
    }
};
