class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const long long mod = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dpa(n + 1, vector<long long>(1001));
        for (int i = 0; i <= nums[0]; ++i) dpa[1][i] = i + 1;
        for (int i = 2; i <= n; ++i) {
            for (int j = 0; j <= nums[i - 1]; ++j) {
                // bug(i, j);
                int lb = (nums[i - 2] + j - nums[i - 1]);
                // lb + 1 ... j
                // if (ub) {
                //     dpa[i & 1][j] = 0;
                //     continue;
                // }
                // bug(i, j, lb);
                lb = min(lb, j);
                if (lb < 0) continue;
                dpa[i][j] = dpa[i - 1][lb];
                // for (int k = 0; k <= j; ++k) {

                //     if (nums[i - 2] - k < nums[i - 1] - j) continue; //break;
                //     // bug(k);
                //     dpa[i][j] = (dpa[i][j] + dpa[i - 1][k]) % mod;
                //     // dpb[i][j] = (dpb[i][j] + dpb[i - 1][k]) % mod;
                //     // bug(dpa[i][j]);
                // }
            }
            for (int j = 1; j < 1001; ++j) {
                dpa[i][j] = (dpa[i][j] + dpa[i ][j - 1]) % mod;
            }
        }

        //printmat(dpa, n + 1, 4);


        return dpa[n][nums.back()];
    }
};
