#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

const int MOD = 1e9 + 7;

class Solution {
  public:
    int countOfPairs(vector<int> &v) {
        int n = v.sz;

        vector<vector<int>> dp(n, vector<int>(1001));
        vector<vector<int>> pre(n, vector<int>(1001));

        for (int x = 0; x <= v[n - 1]; x++)
            dp[n - 1][x] = 1;

        for (int i = n - 2; i >= 0; i--) {
            pre[i + 1][0] = dp[i + 1][0];
            for (int x = 1; x <= 1000; x++)
                pre[i + 1][x] = (dp[i + 1][x] + pre[i + 1][x - 1]) % MOD;

            for (int x = 0; x <= v[i]; x++) {
                dp[i][x] = pre[i + 1][v[i + 1]];

                int k = max(x, v[i + 1] - v[i] + x);
                if (k > 0)
                    dp[i][x] = (dp[i][x] - pre[i + 1][k - 1] + MOD) % MOD;
            }
        }

        int ret = 0;

        for (int x = 0; x <= v[0]; x++)
            ret = (ret + dp[0][x]) % MOD;

        return ret;
    }
};
