class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001, 0));

        for(int i = 0; i <= nums[n-1]; i++) {
            dp[n-1][i] = 1;
        }

        for(int i = n-2; i >= 0; i--) {
            vector<int> suffixSum(1001, 0);
            suffixSum[1000] = dp[i+1][1000];
            for(int k = 999; k >= 0; k--) {
                suffixSum[k] = (suffixSum[k+1] + dp[i+1][k]) % MOD;
            }
            if(nums[i] <= nums[i+1]) {
                int temp = nums[i+1] - nums[i];
                for(int j = 0; j <= nums[i]; j++) {
                    dp[i][j] = suffixSum[temp++];
                }
            } else {
                for(int j = 0; j <= nums[i]; j++) {
                    dp[i][j] = suffixSum[j];
                }
            }
        }

        int ans = 0;
        for(int i = 0; i <= nums[0]; i++) {
            ans = (ans + dp[0][i]) % MOD;
        }

        return ans;
    }
};
