MOD = int(1e9) + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_num = max(nums)
        dp = [1 for _ in range(max_num + 1)]
        ndp = [0 for _ in range(max_num + 1)]
        ps_dp = [0 for _ in range(max_num + 1)]
        for i in range(n - 2, -1, -1):
            for j in range(len(ndp)):
                ndp[j] = 0
                ps_dp[j] = (0 if j == 0 else ps_dp[j - 1]) + dp[j]
            for prev in range(nums[i] + 1):
                prev2 = nums[i] - prev
                lt, rt = max(prev, nums[i + 1] - prev2), nums[i + 1]
                if rt >= lt:
                    ndp[prev] = ps_dp[rt] - (0 if lt == 0 else ps_dp[lt - 1])
            for j in range(len(dp)):
                dp[j] = ndp[j]
        return sum(dp) % MOD
