class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long int n = nums.size();
        long long int mod = 1000000007;
        vector <long long int> diff(n, 0);
        for (long long int i=1; i<n; i++){
            diff[i] = nums[i] - nums[i-1];
        }
        long long int dp[1001][n];
        long long int pre[1001][n];
        for (long long int i=0; i<1001; i++) for (long long int j=0; j<n; j++){
            dp[i][j] = 0;
            pre[i][j] = 0;
        }
        for (long long int i=0; i<=nums[0]; i++){
            dp[i][0] = 1;
        }
        for (int i=0; i<1001; i++) for (int j=0; j<n; j++) pre[i][j] = 0;
        pre[0][0] = 1;
        for (long long int i=1; i<1001; i++) pre[i][0] = ((pre[i-1][0] % mod) + (dp[i][0] % mod)) % mod;
        for (long long int i=1; i<n; i++){
            long long int end = nums[i];
            for (long long int j=0; j<=end; j++){
                long long int end2 = j - max((long long int)0, diff[i]);
                if (end2 >= 0) dp[j][i] = ((dp[j][i] % mod) + (pre[end2][i-1] % mod)) % mod;
                // else dp[j][i] = 0;
                if (j == 0) pre[j][i] = dp[j][i] % mod;
                else pre[j][i] = ((dp[j][i] % mod) + (pre[j-1][i] % mod)) % mod;
            }
            for (int j=end+1; j<1001; j++){
                pre[j][i] = pre[j-1][i];
            }
        }

        long long int sum = 0;
        for (long long int i=0; i<1001; i++) sum = ((sum % mod) + (dp[i][n-1] % mod)) % mod;
        return sum;
    }
};
