#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int dp[n][1024];        // the last number in arr[1] == j        
        int prefix_sum[n][1024];        // the prefix sum of dp

        memset(dp,0,n*1024*sizeof(int));
        memset(prefix_sum,0,n*1024*sizeof(int));

        for(int j=0;j<1024;j++) {
            dp[0][j] = (j<=nums[0]);
            if(j>0) prefix_sum[0][j] = prefix_sum[0][j-1];
            prefix_sum[0][j] += dp[0][j];
        } 

        for(int i=1;i<n;i++) {
            for(int j=0;j<=nums[i];j++) {
                if(j+min(0,nums[i-1]-nums[i]) < 0)  continue;
                dp[i][j] = prefix_sum[i-1][j+min(0,nums[i-1]-nums[i])];
                if(j>0) prefix_sum[i][j] = prefix_sum[i][j-1];
                prefix_sum[i][j] += dp[i][j];
                prefix_sum[i][j] %= MOD;
            }

        }

        int ret = 0;
        for(int j=0;j<1024;j++) {
            ret+=dp[n-1][j];
            ret%=MOD;
        }
        return ret;
    }
};
