M = 10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        nums = [1000] + nums + [1000]
        n = len(nums)
        dp = [[0] * 1001 for _ in range(n)]
        dp[0][0] = 1
        for i in range(1, n):
            dp[i-1] = list(accumulate([0] + dp[i-1], lambda a, b: (a + b) % M))
            for j in range(nums[i]+1):
                dp[i][j] = (dp[i][j] + dp[i-1][max(-1, min(j, nums[i-1], nums[i-1]+j-nums[i]))+1]) % M
            dp[i][j] %= M
        return dp[-1][1000] % M
