class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # dp[i][j]: arr1[i] ends with j, arr2[i] ends with nums[i]-j
        n = len(nums)
        mod = int(1e9+7)
        dp = [[0]*1001 for i in range(n)]
        for j in range(nums[0]+1):
            dp[0][j] = 1
        for i in range(1,n):
            # current sum(arr1[i],arr2[i]) is nums[i]
            idx, cur = 0, 0
            for j in range(nums[i]+1):
                num1,num2 = j,nums[i]-j
                while idx <= j and num2 <= nums[i-1]-idx:
                    cur += dp[i-1][idx]
                    idx += 1
                dp[i][j] = (dp[i][j]+cur)%mod
        # print(dp)
        return sum(dp[n-1])%mod
